('EDUCI49', 'Independent Study in EDUC ', 'Self-directed within the area of study requiring intermittent consultation with a designated instructor.', 12.00, 'T', 0.00, 0),
('EDUCT18', 'Special topics in EDUC ', 'Topics decided upon by faculty will vary within the area of study.', 12.00, 'T', 0.00, 0),
('EDUCT28', 'Special topics in EDUC ', 'Topics decided upon by faculty will vary within the area of study.', 12.00, 'T', 0.00, 0),
